import {HttpClient, HttpErrorResponse} from '@angular/common/http';
import { Injectable } from '@angular/core';
import { jwtDecode } from 'jwt-decode'; // Named import
import {catchError, Observable, throwError} from 'rxjs';
import { tap } from 'rxjs/operators';

@Injectable({
    providedIn: 'root'
})
export class AuthService {
    private tokenKey = 'authToken'; // Key for storing the token
    private apiUrl = 'http://localhost:8087/api/users'; // Your API URL

    constructor(private http: HttpClient) {}

    isLoggedIn(): boolean {
        const token = localStorage.getItem(this.tokenKey);
        if (token) {
            try {
                const decodedToken: any = jwtDecode(token);
                const currentTime = Math.floor(Date.now() / 1000); // Current time in seconds
                return decodedToken.exp > currentTime; // Check if token is expired
            } catch (e) {
                return false; // Token decoding failed, treat as not logged in
            }
        }
        return false; // No token found
    }

    logout(): void {
        localStorage.removeItem(this.tokenKey); // Remove the token from local storage
    }

    login(username: string, password: string): Observable<any> {
        return this.http.post<any>(`${this.apiUrl}/login`, { username, password }).pipe(
            tap(response => {
                if (response && response.token) {
                    localStorage.setItem(this.tokenKey, response.token); // Store token
                }
            }),
            catchError((error: HttpErrorResponse) => {
                console.error('Login error:', error.message);
                return throwError(() => new Error(error.message));
            })
        );
    }



}
